const Header = () => {

    const language = useLanguage()
    const controlButtonsText = language.CONTROL_BUTTONS;
    const connectionStatusText = language.CONNECTION_STATUS;
    const botStatusText = language.BOT_STATUS;
    const buttonText = language.CONNECTION_BUTTON;

    const connectionStatus = ReactRedux.useSelector(state => state.connectionStatus);
    const botStatus = ReactRedux.useSelector(state => state.botStatus);
    const username = ReactRedux.useSelector(state => state.username);
    const joinedChat = ReactRedux.useSelector(state => state.joinedChat);

    const renderStatus = () => {

        if (botStatus !== "STOPPED") {
            return (
                <div className={"connection-status-container"}>
                    <p className={"page-text"}>
                        {connectionStatusText[connectionStatus]}
                        {connectionStatus === "CONNECTED" && <strong>{username}</strong>}
                    </p>
                    {connectionStatus === "CONNECTED" && (
                        <p className={"page-text joined-chat-text"}>
                            {botStatusText[botStatus]}
                            <span className={"joined-chat-span"}>{joinedChat}</span>
                        </p>
                    )}
                </div>
            )
        } else {
            return (
                <div className={"connection-status-container"}>
                    <p className={"page-text"}>
                        {botStatusText[botStatus]}
                    </p>
                </div>
            )
        }
    }

    const renderConnectionButton = () => {

        if (connectionStatus === "DISCONNECTED") {
            return (
                <button
                    className={"connection-button page-text"}
                    onClick={handleRequestTwitchLogin}
                    title={buttonText.CONNECT_TITLE}
                >
                    {buttonText.CONNECT}
                </button>
            )
        } else if (connectionStatus === "CONNECTED") {
            return (
                <button
                    className={"connection-button page-text"}
                    onClick={handleRequestTwitchLogout}
                    title={buttonText.DISCONNECT_TITLE}>
                    {buttonText.DISCONNECT}
                </button>
            )
        } else {
            return null
        }
    }

    return (
        <div className={"header-container"}>

            <div className={"image-and-buttons-container"}>

                <img
                    className={"header-image"}
                    src={"data:image/png;base64,"}
                    alt={"Pikachu"}
                />

                <div className={"bot-status-controls"}>

                    <button
                        title={controlButtonsText.PAUSE}
                        className={`control-button pause-button control-button-${botStatus === "ACTIVE" ? "active" : "inactive"}`}
                        onClick={() => botStatus === "ACTIVE" && handleChangeBotStatus("PAUSED")}
                    >
                        <span className={"pause-button-icon"}/>
                    </button>

                    <button
                        title={controlButtonsText.PLAY}
                        className={`control-button play-button control-button-${botStatus !== "ACTIVE" ? "active" : "inactive"}`}
                        onClick={() => botStatus !== "ACTIVE" && handleChangeBotStatus("ACTIVE")}
                    >
                        <span className={"play-button-icon"}/>
                    </button>

                    <button
                        title={controlButtonsText.STOP}
                        className={`control-button stop-button control-button-${botStatus !== "STOPPED" ? "active" : "inactive"}`}
                        onClick={() => botStatus !== "STOPPED" && handleChangeBotStatus("STOPPED")}
                    >
                        <span className={"stop-button-icon"}/>
                    </button>

                </div>

                <button
                    title={language.CONFIG_BUTTON}
                    className={"config-button"}
                    onClick={handleRequestOpenConfig}
                >

                    <img
                        className={"config-button-img"}
                        src={"data:image/png;base64,"}
                        alt={"Config button"}
                    />
                </button>

            </div>

            <div className={"connection-status-and-button-container"}>
                {renderStatus()}
                {botStatus !== "STOPPED" && renderConnectionButton()}
            </div>

        </div>
    );
}

